

exports.main = function(options, request, callback) {
	if (request.event === "pinf/0/runtime/control/0#events/start") {
		return options.$pinf.getRuntimeConfig(["daemon"], function(err, config) {
			if (err) return callback(err);
			if (config && config.running) {
				return options.$pinf.updateRuntimeConfig(["daemon"], {
					startCount: config.startCount + 1
				}, callback);
			} else {
				return options.$pinf.updateRuntimeConfig(["daemon"], {
					running: true,
					startCount: 1
				}, callback);
			}
		});
	} else
	if (request.event === "pinf/0/runtime/control/0#events/stop") {
		return options.$pinf.clearRuntimeConfig(["daemon"], function(err) {
			if (err) return callback(err);
			return callback(null, {
				running: false
			});
		});
	}
	return callback(new Error("Event '" + request.event + "' not supported!"));
}

